import { useRoute } from 'wouter';
import { useQuery } from '@tanstack/react-query';
import { ArrowLeft, Calendar, Tag, Share2, Loader2 } from 'lucide-react';
import { Helmet } from 'react-helmet-async';
import { Link } from 'wouter';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Card, CardContent } from '@/components/ui/card';
import { Header } from '@/components/Header';
import { Footer } from '@/components/Footer';
import AdSenseAd from '@/components/AdSenseAd';
import GoToTopButton from '@/components/GoToTopButton';
import { BlogPost } from '@shared/schema';

export default function BlogPostDetail() {
  const [, params] = useRoute('/blog/:slug');
  const slug = params?.slug;

  const { data: post, isLoading, error } = useQuery<BlogPost>({
    queryKey: [`/api/blog/posts/${slug}`],
    enabled: !!slug,
  });

  const formatDate = (date: string | Date) => {
    return new Date(date).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'long',
      day: 'numeric',
    });
  };

  const formatContent = (content: string) => {
    const parts = content.split('\n\n');
    return parts.map((part, index) => {
      // Handle images
      if (part.startsWith('![')) {
        const imageMatch = part.match(/!\[([^\]]*)\]\(([^)]+)\)/);
        if (imageMatch) {
          const [, alt, src] = imageMatch;
          return (
            <div key={index} className="my-8">
              <img 
                src={src} 
                alt={alt} 
                className="w-full max-w-4xl mx-auto rounded-lg shadow-lg"
                loading="lazy"
              />
            </div>
          );
        }
      }

      // Handle headings
      if (part.startsWith('### ')) {
        return (
          <h3 key={index} className="text-xl font-bold text-gray-900 dark:text-white mt-8 mb-4">
            {part.substring(4)}
          </h3>
        );
      }

      if (part.startsWith('## ')) {
        return (
          <h2 key={index} className="text-2xl font-bold text-gray-900 dark:text-white mt-10 mb-6">
            {part.substring(3)}
          </h2>
        );
      }

      if (part.startsWith('# ')) {
        return (
          <h1 key={index} className="text-3xl font-bold text-gray-900 dark:text-white mt-8 mb-6">
            {part.substring(2)}
          </h1>
        );
      }

      // Handle lists
      if (part.includes('- **') || part.includes('* **')) {
        const listItems = part.split('\n').filter(line => line.trim().startsWith('- ') || line.trim().startsWith('* '));
        if (listItems.length > 0) {
          return (
            <ul key={index} className="mb-6 space-y-2">
              {listItems.map((item, itemIndex) => {
                const cleanItem = item.replace(/^[*-]\s*/, '');
                const boldMatch = cleanItem.match(/\*\*([^*]+)\*\*:\s*(.*)/);
                if (boldMatch) {
                  const [, boldText, normalText] = boldMatch;
                  return (
                    <li key={itemIndex} className="flex items-start">
                      <span className="text-blue-600 mr-2">‚Ä¢</span>
                      <span className="text-gray-700 dark:text-gray-300">
                        <span className="font-semibold">{boldText}</span>: {normalText}
                      </span>
                    </li>
                  );
                }
                return (
                  <li key={itemIndex} className="flex items-start">
                    <span className="text-blue-600 mr-2">‚Ä¢</span>
                    <span className="text-gray-700 dark:text-gray-300">{cleanItem}</span>
                  </li>
                );
              })}
            </ul>
          );
        }
      }

      // Handle numbered lists
      if (/^\d+\./.test(part.trim())) {
        const listItems = part.split('\n').filter(line => /^\d+\./.test(line.trim()));
        if (listItems.length > 0) {
          return (
            <ol key={index} className="mb-6 space-y-2 list-decimal list-inside">
              {listItems.map((item, itemIndex) => {
                const cleanItem = item.replace(/^\d+\.\s*/, '');
                const boldMatch = cleanItem.match(/\*\*([^*]+)\*\*:\s*(.*)/);
                if (boldMatch) {
                  const [, boldText, normalText] = boldMatch;
                  return (
                    <li key={itemIndex} className="text-gray-700 dark:text-gray-300 ml-4">
                      <span className="font-semibold">{boldText}</span>: {normalText}
                    </li>
                  );
                }
                return (
                  <li key={itemIndex} className="text-gray-700 dark:text-gray-300 ml-4">
                    {cleanItem}
                  </li>
                );
              })}
            </ol>
          );
        }
      }

      // Handle bold text in paragraphs
      const formatBoldText = (text: string) => {
        return text.split(/(\*\*[^*]+\*\*)/).map((segment, segIndex) => {
          if (segment.startsWith('**') && segment.endsWith('**')) {
            return (
              <span key={segIndex} className="font-semibold">
                {segment.slice(2, -2)}
              </span>
            );
          }
          return segment;
        });
      };

      // Regular paragraphs
      if (part.trim()) {
        return (
          <p key={index} className="mb-6 text-gray-700 dark:text-gray-300 leading-relaxed">
            {formatBoldText(part)}
          </p>
        );
      }

      return null;
    }).filter(Boolean);
  };

  if (isLoading) {
    return (
      <div className="min-h-screen bg-background">
        <Header />
        <div className="flex items-center justify-center min-h-[60vh]">
          <Loader2 className="w-8 h-8 animate-spin text-blue-600" />
        </div>
        <Footer />
      </div>
    );
  }

  if (error || !post) {
    return (
      <div className="min-h-screen bg-background">
        <Header />
        <div className="container mx-auto px-4 py-16">
          <div className="text-center">
            <h1 className="text-4xl font-bold text-gray-900 dark:text-white mb-4">
              Post Not Found
            </h1>
            <p className="text-gray-600 dark:text-gray-400 mb-8">
              The blog post you're looking for doesn't exist or has been removed.
            </p>
            <Link href="/blog">
              <Button className="flex items-center gap-2">
                <ArrowLeft className="w-4 h-4" />
                Back to Blog
              </Button>
            </Link>
          </div>
        </div>
        <Footer />
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-background">
      <Helmet>
        <title>{post.title} | Ruvab IT Blog</title>
        <meta name="description" content={post.excerpt} />
        <meta name="keywords" content={post.tags.join(', ')} />
        <meta property="og:title" content={post.title} />
        <meta property="og:description" content={post.excerpt} />
        <meta property="og:type" content="article" />
        <meta property="og:url" content={`https://ruvab.it.com/blog/${post.slug}`} />
        <meta property="article:published_time" content={new Date(post.publishedAt).toISOString()} />
        <meta property="article:section" content={post.category} />
        {post.tags.map((tag, index) => (
          <meta key={index} property="article:tag" content={tag} />
        ))}
        <link rel="canonical" href={`https://ruvab.it.com/blog/${post.slug}`} />
      </Helmet>

      <Header />

      <main className="container mx-auto px-4 py-8">
        <div className="max-w-4xl mx-auto">
          {/* Back Button */}
          <div className="mb-8">
            <Link href="/blog">
              <Button variant="outline" className="flex items-center gap-2">
                <ArrowLeft className="w-4 h-4" />
                Back to Blog
              </Button>
            </Link>
          </div>

          {/* Featured Image Header */}
          <div 
            className="relative w-full h-80 md:h-96 bg-gradient-to-br from-blue-600/90 to-purple-600/90 rounded-lg mb-8 bg-cover bg-center bg-no-repeat overflow-hidden"
            style={{
              backgroundImage: post.featuredImage 
                ? `linear-gradient(to bottom right, rgba(37, 99, 235, 0.9), rgba(126, 34, 206, 0.9)), url(${post.featuredImage})`
                : undefined
            }}
          >
            <div className="absolute inset-0 flex flex-col justify-end p-8 text-white">
              <div className="flex items-center gap-2 mb-4">
                <Badge variant="secondary" className="bg-white/20 text-white border-white/30">
                  {post.category}
                </Badge>
                {post.tags.slice(0, 3).map((tag) => (
                  <Badge key={tag} variant="outline" className="bg-white/10 text-white border-white/30">
                    {tag}
                  </Badge>
                ))}
              </div>

              <h1 className="text-3xl md:text-5xl font-bold mb-4 leading-tight drop-shadow-lg">
                {post.title}
              </h1>

              <div className="flex items-center gap-6 text-white/90">
                <div className="flex items-center gap-2">
                  <Calendar className="w-5 h-5" />
                  <span>{formatDate(post.publishedAt)}</span>
                </div>
                <div className="flex items-center gap-2">
                  <Tag className="w-5 h-5" />
                  <span>{post.category}</span>
                </div>
              </div>
            </div>
          </div>

          {/* Article Content */}
          <article className="prose prose-lg max-w-none mb-12">
            <div className="text-xl text-gray-600 dark:text-gray-400 mb-8 font-medium leading-relaxed">
              {post.excerpt}
            </div>

            {/* Ad placement */}
            <div className="my-8">
              <AdSenseAd />
            </div>

            <div className="text-gray-700 dark:text-gray-300 leading-relaxed">
              {formatContent(post.content)}
            </div>
          </article>

          {/* Share Section */}
          <div className="border-t pt-8 mb-8">
            <div className="flex items-center justify-between">
              <div>
                <h3 className="text-lg font-semibold text-gray-900 dark:text-white mb-2">
                  Share this article
                </h3>
                <p className="text-gray-600 dark:text-gray-400">
                  Help others discover this content
                </p>
              </div>
              <Button variant="outline" className="flex items-center gap-2">
                <Share2 className="w-4 h-4" />
                Share
              </Button>
            </div>
          </div>

          {/* Related Articles */}
          <Card>
            <CardContent className="p-6">
              <h3 className="text-lg font-semibold text-gray-900 dark:text-white mb-4">
                Related Articles
              </h3>
              <div className="text-center text-gray-600 dark:text-gray-400 py-8">
                <p>More articles coming soon...</p>
                <Link href="/blog" className="mt-4 inline-block">
                  <Button variant="outline">Browse All Articles</Button>
                </Link>
              </div>
            </CardContent>
          </Card>
        </div>
      </main>

      <Footer />
      <GoToTopButton />
    </div>
  );
}

client/src/components/ChatWidget.tsx.content:
import React, { useState, useEffect, useRef } from 'react';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Input } from '@/components/ui/input';
import { Badge } from '@/components/ui/badge';
import { MessageCircle, Send, X, Minimize2, User, Bot, ExternalLink } from 'lucide-react';

interface Message {
  id: string;
  text: string;
  sender: 'user' | 'support';
  timestamp: Date;
}

const ChatWidget: React.FC = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [isMinimized, setIsMinimized] = useState(false);
  const [messages, setMessages] = useState<Message[]>([]);
  const [newMessage, setNewMessage] = useState('');
  const [isTyping, setIsTyping] = useState(false);
  const [unreadCount, setUnreadCount] = useState(0);
  const messagesEndRef = useRef<HTMLDivElement>(null);

  useEffect(() => {
    if (isOpen) {
      // Load conversation history on first open
      loadConversationHistory();
    }
  }, [isOpen]);

  const loadConversationHistory = async () => {
    try {
      // Get session ID from storage
      let sessionId = sessionStorage.getItem('chat_session_id');

      if (!sessionId) {
        // No session yet, show welcome message
        if (messages.length === 0) {
          setTimeout(() => {
            addMessage({
              id: 'welcome',
              text: "Hi! üëã I'm here to help you with any questions about Ruvab IT's services. Ask me about AI solutions, software development, or our tools!",
              sender: 'support',
              timestamp: new Date()
            });
          }, 500);
        }
        return;
      }

      // Fetch conversation history
      const response = await fetch(`/api/chat/conversation/${sessionId}`);

      if (response.ok) {
        const data = await response.json();

        if (data.success && data.messages && data.messages.length > 0) {
          // Load historical messages into state
          const historicalMessages: Message[] = data.messages.map((msg: any) => ({
            id: msg.id.toString(),
            text: msg.sender === 'user' ? msg.message : (msg.response || ''),
            sender: msg.sender === 'user' ? 'user' : 'support',
            timestamp: new Date(msg.createdAt)
          }));

          // Set historical messages
          setMessages(historicalMessages);
        } else if (messages.length === 0) {
          // No history, show welcome message
          setTimeout(() => {
            addMessage({
              id: 'welcome',
              text: "Hi! üëã I'm here to help you with any questions about Ruvab IT's services. Ask me about AI solutions, software development, or our tools!",
              sender: 'support',
              timestamp: new Date()
            });
          }, 500);
        }
      } else {
        // Error fetching history, show welcome message if empty
        if (messages.length === 0) {
          setTimeout(() => {
            addMessage({
              id: 'welcome',
              text: "Hi! üëã I'm here to help you with any questions about Ruvab IT's services. Ask me about AI solutions, software development, or our tools!",
              sender: 'support',
              timestamp: new Date()
            });
          }, 500);
        }
      }
    } catch (error) {
      console.error('Error loading conversation history:', error);
      // Show welcome message on error if empty
      if (messages.length === 0) {
        setTimeout(() => {
          addMessage({
            id: 'welcome',
            text: "Hi! üëã I'm here to help you with any questions about Ruvab IT's services. Ask me about AI solutions, software development, or our tools!",
            sender: 'support',
            timestamp: new Date()
          });
        }, 500);
      }
    }
  };

  useEffect(() => {
    scrollToBottom();
    if (isOpen) {
      setUnreadCount(0);
    }
  }, [messages, isOpen]);

  const scrollToBottom = () => {
    setTimeout(() => {
      messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
    }, 100);
  };

  const addMessage = (message: Message) => {
    setMessages(prev => [...prev, message]);

    if (!isOpen && message.sender === 'support') {
      setUnreadCount(prev => prev + 1);
    }
  };

  const sendMessage = async () => {
    if (!newMessage.trim()) return;

    const userMessage: Message = {
      id: Math.random().toString(36).substr(2, 9),
      text: newMessage,
      sender: 'user',
      timestamp: new Date()
    };

    addMessage(userMessage);
    setNewMessage('');

    // Show typing indicator
    setIsTyping(true);

    try {
      // Generate session ID if not exists (store in sessionStorage)
      let sessionId = sessionStorage.getItem('chat_session_id');
      if (!sessionId) {
        sessionId = 'session_' + Math.random().toString(36).substr(2, 9);
        sessionStorage.setItem('chat_session_id', sessionId);
      }

      // Call AI API
      const response = await fetch('/api/chat/message', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          sessionId,
          message: userMessage.text,
        }),
      });

      const data = await response.json();

      if (data.success) {
        addMessage({
          id: Math.random().toString(36).substr(2, 9),
          text: data.response,
          sender: 'support',
          timestamp: new Date()
        });
      } else {
        // Fallback to error message
        addMessage({
          id: Math.random().toString(36).substr(2, 9),
          text: "Sorry, I'm having trouble processing your request. Please try again or contact support@ruvabit.com.",
          sender: 'support',
          timestamp: new Date()
        });
      }
    } catch (error) {
      console.error('Chat error:', error);
      addMessage({
        id: Math.random().toString(36).substr(2, 9),
        text: "I'm experiencing technical difficulties. Please contact support@ruvabit.com for assistance.",
        sender: 'support',
        timestamp: new Date()
      });
    } finally {
      setIsTyping(false);
    }
  };

  const handleKeyPress = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      sendMessage();
    }
  };

  const openFullChat = () => {
    window.open('/live-chat', '_blank');
  };

  // Chat button when closed
  if (!isOpen) {
    return (
      <div className="fixed bottom-6 right-6 z-50">
        <Button
          onClick={() => setIsOpen(true)}
          className="rounded-full h-14 w-14 bg-blue-600 hover:bg-blue-700 shadow-lg"
        >
          <MessageCircle className="h-6 w-6" />
          {unreadCount > 0 && (
            <Badge className="absolute -top-2 -right-2 h-6 w-6 rounded-full bg-red-500 text-white text-xs flex items-center justify-center">
              {unreadCount > 9 ? '9+' : unreadCount}
            </Badge>
          )}
        </Button>
      </div>
    );
  }

  // Chat widget when open
  return (
    <div className="fixed bottom-6 right-6 z-50">
      <Card className={`w-80 transition-all duration-300 ${
        isMinimized ? 'h-14' : 'h-[400px]'
      } shadow-xl border-0 bg-white`}>
        {/* Header */}
        <CardHeader className="pb-2 bg-blue-600 text-white rounded-t-lg">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-2">
              <MessageCircle className="h-4 w-4" />
              <div>
                <CardTitle className="text-sm">Ruvab IT Support</CardTitle>
                <CardDescription className="text-xs text-blue-100">
                  We're here to help!
                </CardDescription>
              </div>
            </div>

            <div className="flex items-center space-x-1">
              <Button
                onClick={openFullChat}
                variant="ghost"
                size="sm"
                className="h-6 w-6 p-0 text-white hover:bg-blue-700"
                title="Open Full Chat"
              >
                <ExternalLink className="h-3 w-3" />
              </Button>

              <Button
                onClick={() => setIsMinimized(!isMinimized)}
                variant="ghost"
                size="sm"
                className="h-6 w-6 p-0 text-white hover:bg-blue-700"
              >
                <Minimize2 className="h-3 w-3" />
              </Button>

              <Button
                onClick={() => setIsOpen(false)}
                variant="ghost"
                size="sm"
                className="h-6 w-6 p-0 text-white hover:bg-blue-700"
              >
                <X className="h-3 w-3" />
              </Button>
            </div>
          </div>
        </CardHeader>

        {!isMinimized && (
          <>
            {/* Messages */}
            <CardContent className="h-72 overflow-y-auto p-3 space-y-3">
              {messages.map((message) => (
                <div
                  key={message.id}
                  className={`flex ${
                    message.sender === 'user' ? 'justify-end' : 'justify-start'
                  }`}
                >
                  <div
                    className={`max-w-[80%] p-2 rounded-lg text-sm ${
                      message.sender === 'user'
                        ? 'bg-blue-600 text-white'
                        : 'bg-gray-100 text-gray-900'
                    }`}
                  >
                    <div className="flex items-start space-x-1">
                      {message.sender === 'support' && (
                        <Bot className="h-3 w-3 mt-0.5 flex-shrink-0" />
                      )}
                      <div>
                        <p className="whitespace-pre-wrap">{message.text}</p>
                        <p className="text-xs opacity-70 mt-1">
                          {message.timestamp.toLocaleTimeString([], { 
                            hour: '2-digit', 
                            minute: '2-digit' 
                          })}
                        </p>
                      </div>
                    </div>
                  </div>
                </div>
              ))}

              {isTyping && (
                <div className="flex justify-start">
                  <div className="bg-gray-100 text-gray-900 p-2 rounded-lg text-sm">
                    <div className="flex items-center space-x-2">
                      <Bot className="h-3 w-3" />
                      <div className="flex space-x-1">
                        <div className="w-2 h-2 bg-gray-400 rounded-full animate-bounce"></div>
                        <div className="w-2 h-2 bg-gray-400 rounded-full animate-bounce" style={{ animationDelay: '0.1s' }}></div>
                        <div className="w-2 h-2 bg-gray-400 rounded-full animate-bounce" style={{ animationDelay: '0.2s' }}></div>
                      </div>
                    </div>
                  </div>
                </div>
              )}

              <div ref={messagesEndRef} />
            </CardContent>

            {/* Input */}
            <div className="p-3 border-t">
              <div className="flex items-center space-x-2">
                <Input
                  value={newMessage}
                  onChange={(e) => setNewMessage(e.target.value)}
                  onKeyPress={handleKeyPress}
                  placeholder="Type your message..."
                  className="text-sm flex-1 h-9"
                  disabled={isTyping}
                />
                <Button
                  onClick={sendMessage}
                  disabled={!newMessage.trim() || isTyping}
                  size="sm"
                  className="px-3 h-9 w-9 flex items-center justify-center"
                >
                  <Send className="h-3 w-3" />
                </Button>
              </div>
            </div>
          </>
        )}
      </Card>
    </div>
  );
};

export default ChatWidget;

