    <section id="services" className="py-12 sm:py-16 lg:py-20 bg-gray-50">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="text-center mb-12 sm:mb-16">
          <h2 className="text-2xl sm:text-3xl lg:text-4xl font-bold text-gray-900 mb-4">Solutions & Partnerships</h2>
          <p className="text-base sm:text-lg lg:text-xl text-gray-600 max-w-4xl mx-auto">
            Promoting innovative solutions and fostering collaborations with founders in AI, automation, business intelligence, cloud solutions, and cybersecurity.
          </p>
        </div>

        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6 sm:gap-8">
          {services.map((service, index) => {
            const Icon = service.icon;
            return (
              <div key={index} className="bg-white p-6 sm:p-8 rounded-xl shadow-lg hover:shadow-xl transition-shadow">
                <div className={`w-10 h-10 sm:w-12 sm:h-12 ${service.color} rounded-lg flex items-center justify-center mb-4 sm:mb-6`}>
                  <Icon className="w-5 h-5 sm:w-6 sm:h-6 text-white" />
                </div>
                <h3 className="text-lg sm:text-xl font-bold text-gray-900 mb-3 sm:mb-4">{service.title}</h3>
                <p className="text-sm sm:text-base text-gray-600 leading-relaxed">{service.description}</p>
              </div>
            );
          })}
        </div>
      </div>
    </section>
  );
}

client/src/pages/FreeToolsPage.tsx.content:
import { useQuery } from "@tanstack/react-query";
import { Helmet } from "react-helmet-async";
import { Loader2, Wrench, ExternalLink, ArrowLeft, Home, Briefcase } from "lucide-react";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { useState } from "react";
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Link, useLocation } from "wouter";

interface FreeTool {
  id: number;
  title: string;
  description: string | null;
  htmlCode: string;
  category: string | null;
  isActive: boolean;
  submittedAt: string;
  webhookSource: string | null;
  metadata: any;
}

export default function FreeToolsPage() {
  const [selectedTool, setSelectedTool] = useState<FreeTool | null>(null);

  const { data: tools, isLoading } = useQuery<FreeTool[]>({
    queryKey: ["/api/tools"],
  });

  const handleToolClick = (tool: FreeTool) => {
    setSelectedTool(tool);
  };

  const handleCloseDialog = () => {
    setSelectedTool(null);
  };

  return (
    <>
      <Helmet>
        <title>Free Tools - Ruvab IT | Productivity Utilities</title>
        <meta
          name="description"
          content="Access a collection of free tools and utilities to boost your productivity and streamline your workflow."
        />
      </Helmet>

      <div className="min-h-screen bg-gradient-to-br from-blue-50 via-white to-purple-50 dark:from-gray-900 dark:via-gray-800 dark:to-gray-900">
        <div className="container mx-auto px-4 py-12">
          {/* Navigation */}
          <div className="flex items-center gap-4 mb-8">
            <Button
              variant="outline"
              onClick={() => window.history.back()}
              className="flex items-center gap-2"
              data-testid="button-back"
            >
              <ArrowLeft className="w-4 h-4" />
              Back
            </Button>
            <div className="flex gap-2">
              <Link href="/">
                <Button variant="ghost" className="flex items-center gap-2" data-testid="button-home">
                  <Home className="w-4 h-4" />
                  Home
                </Button>
              </Link>
              <Link href="/services">
                <Button variant="ghost" className="flex items-center gap-2" data-testid="button-services">
                  <Briefcase className="w-4 h-4" />
                  Services
                </Button>
              </Link>
            </div>
          </div>

          {/* Header */}
          <div className="text-center mb-12">
            <div className="inline-flex items-center gap-2 bg-gradient-to-r from-blue-600 to-purple-600 text-white px-6 py-2 rounded-full mb-4">
              <Wrench className="w-5 h-5" />
              <span className="font-semibold">Free Tools Collection</span>
            </div>
            <h1 className="text-4xl md:text-5xl font-bold mb-4 bg-gradient-to-r from-blue-600 to-purple-600 bg-clip-text text-transparent">
              Productivity Tools
            </h1>
            <p className="text-lg text-gray-600 dark:text-gray-300 max-w-2xl mx-auto">
              Explore our collection of free, powerful tools to enhance your workflow and boost productivity.
            </p>
          </div>

          {/* Loading State */}
          {isLoading && (
            <div className="flex items-center justify-center py-20">
              <Loader2 className="w-8 h-8 animate-spin text-blue-600" />
              <span className="ml-3 text-gray-600 dark:text-gray-300">Loading tools...</span>
            </div>
          )}

          {/* Tools Grid */}
          {!isLoading && tools && tools.length > 0 && (
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
              {tools.map((tool) => (
                <Card
                  key={tool.id}
                  className="group hover:shadow-xl transition-all duration-300 cursor-pointer border-2 hover:border-blue-400 dark:hover:border-purple-500"
                  onClick={() => handleToolClick(tool)}
                  data-testid={`card-tool-${tool.id}`}
                >
                  <CardHeader>
                    <div className="flex items-start justify-between mb-2">
                      <CardTitle className="text-xl font-bold text-gray-900 dark:text-white group-hover:text-blue-600 dark:group-hover:text-purple-400 transition-colors">
                        {tool.title}
                      </CardTitle>
                      <ExternalLink className="w-5 h-5 text-gray-400 group-hover:text-blue-600 dark:group-hover:text-purple-400 transition-colors" />
                    </div>
                    {tool.category && (
                      <Badge
                        variant="secondary"
                        className="w-fit bg-gradient-to-r from-blue-100 to-purple-100 dark:from-blue-900 dark:to-purple-900 text-blue-700 dark:text-purple-300"
                      >
                        {tool.category}
                      </Badge>
                    )}
                  </CardHeader>
                  <CardContent>
                    <CardDescription className="text-gray-600 dark:text-gray-300 line-clamp-3">
                      {tool.description || "Click to view and use this tool"}
                    </CardDescription>
                    <Button
                      variant="ghost"
                      className="mt-4 w-full group-hover:bg-gradient-to-r group-hover:from-blue-600 group-hover:to-purple-600 group-hover:text-white transition-all"
                      data-testid={`button-open-tool-${tool.id}`}
                    >
                      Open Tool
                    </Button>
                  </CardContent>
                </Card>
              ))}
            </div>
          )}

          {/* Empty State */}
          {!isLoading && tools && tools.length === 0 && (
            <div className="text-center py-20">
              <Wrench className="w-16 h-16 mx-auto text-gray-400 mb-4" />
              <h3 className="text-2xl font-semibold text-gray-700 dark:text-gray-300 mb-2">
                No Tools Available Yet
              </h3>
              <p className="text-gray-600 dark:text-gray-400">
                New tools will be added soon. Check back later!
              </p>
            </div>
          )}
        </div>
      </div>

      {/* Tool Preview Dialog */}
      <Dialog open={!!selectedTool} onOpenChange={handleCloseDialog}>
        <DialogContent className="max-w-6xl max-h-[90vh] overflow-hidden flex flex-col">
          <DialogHeader>
            <DialogTitle className="text-2xl font-bold">{selectedTool?.title}</DialogTitle>
            {selectedTool?.description && (
              <p className="text-gray-600 dark:text-gray-400">{selectedTool.description}</p>
            )}
          </DialogHeader>
          <div className="flex-1 overflow-auto border rounded-lg">
            {selectedTool && (
              <iframe
                srcDoc={selectedTool.htmlCode}
                title={selectedTool.title}
                className="w-full h-full min-h-[500px]"
                sandbox="allow-scripts allow-same-origin"
                data-testid="iframe-tool-preview"
              />
            )}
          </div>
        </DialogContent>
      </Dialog>
    </>
  );
}

client/src/components/ui/breadcrumb.tsx.content:
import * as React from "react"
import { Slot } from "@radix-ui/react-slot"
import { ChevronRight, MoreHorizontal } from "lucide-react"

import { cn } from "@/lib/utils"

const Breadcrumb = React.forwardRef<
  HTMLElement,
  React.ComponentPropsWithoutRef<"nav"> & {
    separator?: React.ReactNode
  }
>(({ ...props }, ref) => <nav ref={ref} aria-label="breadcrumb" {...props} />)
Breadcrumb.displayName = "Breadcrumb"

const BreadcrumbList = React.forwardRef<
  HTMLOListElement,
  React.ComponentPropsWithoutRef<"ol">
>(({ className, ...props }, ref) => (
  <ol
    ref={ref}
    className={cn(
      "flex flex-wrap items-center gap-1.5 break-words text-sm text-muted-foreground sm:gap-2.5",
      className
    )}
    {...props}
  />
))
BreadcrumbList.displayName = "BreadcrumbList"

const BreadcrumbItem = React.forwardRef<
  HTMLLIElement,
  React.ComponentPropsWithoutRef<"li">
>(({ className, ...props }, ref) => (
  <li
    ref={ref}
    className={cn("inline-flex items-center gap-1.5", className)}
    {...props}
  />
))
BreadcrumbItem.displayName = "BreadcrumbItem"

const BreadcrumbLink = React.forwardRef<
  HTMLAnchorElement,
  React.ComponentPropsWithoutRef<"a"> & {
    asChild?: boolean
  }
>(({ asChild, className, ...props }, ref) => {
  const Comp = asChild ? Slot : "a"

  return (
    <Comp
      ref={ref}
      className={cn("transition-colors hover:text-foreground", className)}
      {...props}
    />
  )
})
BreadcrumbLink.displayName = "BreadcrumbLink"

const BreadcrumbPage = React.forwardRef<
  HTMLSpanElement,
  React.ComponentPropsWithoutRef<"span">
>(({ className, ...props }, ref) => (
  <span
    ref={ref}
    role="link"
    aria-disabled="true"
    aria-current="page"
    className={cn("font-normal text-foreground", className)}
    {...props}
  />
))
BreadcrumbPage.displayName = "BreadcrumbPage"

const BreadcrumbSeparator = ({
  children,
  className,
  ...props
}: React.ComponentProps<"li">) => (
  <li
    role="presentation"
    aria-hidden="true"
    className={cn("[&>svg]:w-3.5 [&>svg]:h-3.5", className)}
    {...props}
  >
    {children ?? <ChevronRight />}
  </li>
)
BreadcrumbSeparator.displayName = "BreadcrumbSeparator"

const BreadcrumbEllipsis = ({
  className,
  ...props
}: React.ComponentProps<"span">) => (
  <span
    role="presentation"
    aria-hidden="true"
    className={cn("flex h-9 w-9 items-center justify-center", className)}
    {...props}
  >
    <MoreHorizontal className="h-4 w-4" />
    <span className="sr-only">More</span>
  </span>
)
BreadcrumbEllipsis.displayName = "BreadcrumbElipssis"

export {
  Breadcrumb,
  BreadcrumbList,
  BreadcrumbItem,
  BreadcrumbLink,
  BreadcrumbPage,
  BreadcrumbSeparator,
  BreadcrumbEllipsis,
}

client/src/components/SubscriptionHandle.content:
import { useState } from 'react';
import { useMutation, useQuery, useQueryClient } from '@tanstack/react-query';
import { apiRequest } from '@/lib/queryClient';
import { useAuth } from '@/contexts/AuthContext';
import { AuthModal } from '@/components/AuthModal';
import { useToast } from '@/hooks/use-toast';
import { Skeleton } from '@/components/ui/skeleton';
import { Loader2 } from 'lucide-react';
import { detectUserLocation, getTaxInfo, getTaxMessage, formatRupees, type LocationData } from '@shared/pricing';

declare global {
  interface Window {
    Razorpay: any;
  }
}

interface Plan {
  id: number;
  name: string;
  planType: string;
  priceMin: number;
  priceMax: number;
  currency: string;
  billingInterval: string;
  description: string;
  features: string[];
}

interface SubscriptionHandlerProps {
  planId: number;
  planName: string;
  onSuccess?: () => void;
}

export function SubscriptionHandler({ planId, planName, onSuccess }: SubscriptionHandlerProps) {
  const [showAuthModal, setShowAuthModal] = useState(false);
  const [isProcessing, setIsProcessing] = useState(false);
  const [userLocation, setUserLocation] = useState<LocationData | null>(null);
  const [locationLoading, setLocationLoading] = useState(true);
  const { user } = useAuth();
  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Detect user location on component mount
  useEffect(() => {
    const detectLocation = async () => {
      try {
        setLocationLoading(true);
        const location = await detectUserLocation();
        setUserLocation(location);
      } catch (error) {
        console.warn('Location detection failed:', error);
        // Fallback to India
        setUserLocation({
          country: 'India',
          countryCode: 'IN',
          region: '',
          city: '',
          timezone: 'Asia/Kolkata'
        });
      } finally {
        setLocationLoading(false);
      }
    };

    detectLocation();
  }, []);

  // Get plan details
  const { data: plan, isLoading: planLoading } = useQuery<Plan>({
    queryKey: [`/api/subscriptions/plans/${planId}`],
    enabled: !!planId
  });

  // Create subscription mutation
  const createSubscriptionMutation = useMutation({
    mutationFn: async (planId: number) => {
      const response = await apiRequest('POST', '/api/subscriptions/create', { planId });
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/subscriptions/my'] });
    }
  });

  // Create payment order mutation
  const createPaymentMutation = useMutation({
    mutationFn: async (subscriptionId: number) => {
      const response = await apiRequest('POST', '/api/subscriptions/payment/create', { subscriptionId });
      return response.json();
    }
  });

  // Verify payment mutation
  const verifyPaymentMutation = useMutation({
    mutationFn: async (paymentData: any) => {
      const response = await apiRequest('POST', '/api/subscriptions/payment/verify', paymentData);
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/subscriptions/my'] });
      queryClient.invalidateQueries({ queryKey: ['/api/subscriptions/payments'] });
      toast({
        title: "Payment Successful!",
        description: `Your subscription to ${planName} has been activated.`,
      });
      onSuccess?.();
    }
  });

  const handleSubscribe = async () => {
    if (!user) {
      setShowAuthModal(true);
      return;
    }

    // Allow proceeding even if plan details aren't loaded (non-blocking)
    if (planLoading) {
      toast({
        title: "Loading",
        description: "Plan details are loading. Please wait a moment.",
        variant: "default"
      });
      return;
    }

    setIsProcessing(true);

    try {
      // Step 1: Create subscription
      const subscriptionResult = await createSubscriptionMutation.mutateAsync(planId);

      if (!subscriptionResult.success) {
        throw new Error(subscriptionResult.error || 'Failed to create subscription');
      }

      const subscription = subscriptionResult.subscription;

      // Step 2: Create payment order
      const paymentResult = await createPaymentMutation.mutateAsync(subscription.id);

      if (!paymentResult.success) {
        throw new Error(paymentResult.error || 'Failed to create payment order');
      }

      // Step 3: Open Razorpay checkout
      const options = {
        key: paymentResult.key,
        amount: paymentResult.amount,
        currency: paymentResult.currency,
        name: 'Ruvab IT',
        description: `Subscription to ${plan?.name || planName} plan`,
        order_id: paymentResult.razorpayOrderId,
        prefill: {
          name: `${user.firstName || ''} ${user.lastName || ''}`.trim() || user.username,
          email: user.email,
        },
        theme: {
          color: '#3B82F6'
        },
        handler: async function (response: any) {
          try {
            // Step 4: Verify payment
            await verifyPaymentMutation.mutateAsync({
              razorpay_order_id: response.razorpay_order_id,
              razorpay_payment_id: response.razorpay_payment_id,
              razorpay_signature: response.razorpay_signature,
              orderId: paymentResult.orderId,
              subscriptionId: subscription.id
            });
          } catch (error) {
            console.error('Payment verification error:', error);
            toast({
              title: "Payment Verification Failed",
              description: "Your payment was processed but verification failed. Please contact support.",
              variant: "destructive"
            });
          } finally {
            setIsProcessing(false);
          }
        },
        modal: {
          ondismiss: function() {
            setIsProcessing(false);
          }
        }
      };

      // Check if Razorpay is available
      if (!window.Razorpay) {
        toast({
          title: "Payment Gateway Unavailable", 
          description: "Payment system is not available. Please check your internet connection and try again.",
          variant: "destructive"
        });
        setIsProcessing(false);
        return;
      }

      const rzp = new window.Razorpay(options);
      rzp.open();

    } catch (error: any) {
      console.error('Subscription error:', error);
      toast({
        title: "Subscription Failed",
        description: error.message || "Something went wrong. Please try again.",
        variant: "destructive"
      });
      setIsProcessing(false);
    }
  };


  // Helper function to calculate GST breakdown for display
  const calculateGSTBreakdown = (baseAmount: number) => {
    const gstAmount = Math.round(baseAmount * 0.18); // 18% GST
    const totalAmount = baseAmount + gstAmount;
    return { baseAmount, gstAmount, totalAmount };
  };

  const formatIndianCurrency = (amountInPaise: number) => {
    return `‚Çπ${(amountInPaise / 100).toLocaleString('en-IN')}`;
  };

  return (
    <>
      <div className="text-center">
        <div className="mb-4 p-4 bg-gray-50 dark:bg-gray-800 rounded-lg border">
          {plan ? (
            <>
              <div className="text-sm text-gray-600 dark:text-gray-400 mb-2">
                <strong>{plan.name} Plan</strong> - {plan.billingInterval}
              </div>
              {(() => {
                const breakdown = calculateGSTBreakdown(plan.priceMin);
                return (
                  <div className="text-sm space-y-1">
                    <div className="flex justify-between">
                      <span>Base Price:</span>
                      <span>{formatIndianCurrency(breakdown.baseAmount)}</span>
                    </div>
                    <div className="flex justify-between text-orange-600 dark:text-orange-400">
                      <span>GST (18%):</span>
                      <span>+ {formatIndianCurrency(breakdown.gstAmount)}</span>
                    </div>
                    <hr className="border-gray-300 dark:border-gray-600" />
                    <div className="flex justify-between font-semibold text-lg">
                      <span>Total Amount:</span>
                      <span className="text-green-600 dark:text-green-400">
                        {formatIndianCurrency(breakdown.totalAmount)}
                      </span>
                    </div>
                    <div className="text-xs text-gray-500 dark:text-gray-400 mt-1">
                      *GST included as per Indian tax regulations
                    </div>
                  </div>
                );
              })()}
            </>
          ) : (
            <>
              <div className="text-sm text-gray-600 dark:text-gray-400 mb-2">
                <Skeleton className="h-4 w-32" />
              </div>
              <div className="text-sm space-y-2">
                <div className="flex justify-between">
                  <Skeleton className="h-4 w-20" />
                  <Skeleton className="h-4 w-16" />
                </div>
                <div className="flex justify-between">
                  <Skeleton className="h-4 w-16" />
                  <Skeleton className="h-4 w-14" />
                </div>
                <hr className="border-gray-300 dark:border-gray-600" />
                <div className="flex justify-between">
                  <Skeleton className="h-5 w-24" />
                  <Skeleton className="h-5 w-18" />
                </div>
                <Skeleton className="h-3 w-full" />
              </div>
            </>
          )}
        </div>

        <button
          onClick={handleSubscribe}
          disabled={isProcessing || planLoading || createSubscriptionMutation.isPending || createPaymentMutation.isPending}
          className="w-full bg-blue-600 hover:bg-blue-700 disabled:bg-blue-300 disabled:cursor-not-allowed text-white font-semibold py-2 px-4 rounded-lg transition-all duration-200 flex items-center justify-center gap-2"
          data-testid={`button-subscribe-${planName.toLowerCase().replace(' ', '-')}`}
        >
          {(isProcessing || planLoading || createSubscriptionMutation.isPending || createPaymentMutation.isPending) && (
            <Loader2 className="h-4 w-4 animate-spin" />
          )}
          {isProcessing ? 'Processing...' : 
           planLoading ? 'Loading...' :
           createSubscriptionMutation.isPending ? 'Creating Subscription...' :
           createPaymentMutation.isPending ? 'Preparing Payment...' :
           `Subscribe to ${planName}`}
        </button>
      </div>

      <AuthModal
        isOpen={showAuthModal}
        onClose={() => setShowAuthModal(false)}
        defaultMode="register"
        onSuccess={() => {
          setShowAuthModal(false);
          // Try subscribing again after successful authentication
          setTimeout(handleSubscribe, 100);
        }}
      />
    </>
  );
}

shared/schema.ts.content:
import { pgTable, text, serial, integer, boolean, timestamp, varchar, index, jsonb, uniqueIndex, decimal } from "drizzle-orm/pg-core";
import { createInsertSchema } from "drizzle-zod";
import { z } from "zod";
import { sql } from "drizzle-orm";
import { relations } from "drizzle-orm";

// Users table for authentication and admin functionality
export const users = pgTable("users", {
  id: serial("id").primaryKey(),
  username: varchar("username", { length: 255 }).notNull().unique(),
  email: varchar("email", { length: 255 }).unique(),
  password: text("password").notNull(),
  firstName: varchar("first_name", { length: 255 }),
  lastName: varchar("last_name", { length: 255 }),
  isAdmin: boolean("is_admin").default(false).notNull(),
  createdAt: timestamp("created_at").defaultNow().notNull(),
  updatedAt: timestamp("updated_at").defaultNow().notNull(),
});

// Session storage table for authentication
export const sessions = pgTable(
  "sessions",
  {
    sid: varchar("sid").primaryKey(),
    sess: jsonb("sess").notNull(),
    expire: timestamp("expire").notNull(),
  },
  (table) => [index("IDX_session_expire").on(table.expire)],
);

// Blog posts table
export const blogPosts = pgTable("blog_posts", {
  id: serial("id").primaryKey(),
  title: text("title").notNull(),
  slug: text("slug").notNull().unique(),
  excerpt: text("excerpt").notNull(),
  content: text("content").notNull(),
  category: text("category").notNull(),
  tags: text("tags").array().default([]).notNull(),
  featuredImage: text("featured_image"),
  publishedAt: timestamp("published_at").defaultNow().notNull(),
  updatedAt: timestamp("updated_at").defaultNow().notNull(),
  isPublished: boolean("is_published").default(false).notNull(),
  authorId: integer("author_id").notNull().references(() => users.id),
  source: varchar("source", { length: 50 }).default("manual").notNull(),
});

export const blogSchedule = pgTable("blog_schedule", {
  id: serial("id").primaryKey(),
  lastGeneratedDate: timestamp("last_generated_date"),
  lastGeneratedCategory: varchar("last_generated_category", { length: 100 }),
  nextScheduledDate: timestamp("next_scheduled_date"),
  totalGenerated: integer("total_generated").default(0).notNull(),
  failedAttempts: integer("failed_attempts").default(0).notNull(),
  lastWebhookReceived: timestamp("last_webhook_received"),
  isActive: boolean("is_active").default(true).notNull(),
  createdAt: timestamp("created_at").defaultNow().notNull(),
  updatedAt: timestamp("updated_at").defaultNow().notNull(),
});

// CMS page content management
export const pageContents = pgTable("page_contents", {
  id: serial("id").primaryKey(),
  pageKey: varchar("page_key", { length: 255 }).notNull().unique(),
  title: text("title").notNull(),
  content: jsonb("content").notNull(),
  isActive: boolean("is_active").default(true).notNull(),
  updatedAt: timestamp("updated_at").defaultNow().notNull(),
  updatedBy: integer("updated_by").notNull().references(() => users.id),
});

// Search indexing table for advanced search functionality
export const searchIndex = pgTable("search_index", {
  id: serial("id").primaryKey(),
  contentType: varchar("content_type", { length: 50 }).notNull(), // 'blog', 'page', 'service'
  contentId: integer("content_id").notNull(),
  title: text("title").notNull(),
  content: text("content").notNull(),
  searchVector: text("search_vector"), // For full-text search
  updatedAt: timestamp("updated_at").defaultNow().notNull(),
}, (table) => [
  index("search_content_idx").on(table.contentType, table.contentId),
]);

// Relations
