import sanitizeHtml from 'sanitize-html';
import { db } from './db';
import { webhookJobs, blogPosts, users, webhookRequests } from '../shared/schema';
import { eq } from 'drizzle-orm';
import fetch from 'node-fetch';
import { recordWebhookReceived } from './blog-scheduler';
import { stripHtmlTags, stripHtmlFromArray } from './html-utils';

const CALLBACK_MAX_RETRIES = 3;
const CALLBACK_RETRY_DELAYS = [2000, 4000, 8000];

function generateSlug(title: string): string {
  return title
    .toLowerCase()
    .replace(/[^a-z0-9]+/g, '-')
    .replace(/^-|-$/g, '')
    .substring(0, 200);
}

async function sendCallback(url: string, payload: any, attempt = 0): Promise<boolean> {
  try {
    const response = await fetch(url, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(payload),
    });

    return response.ok;
  } catch (error) {
    console.error(`Callback attempt ${attempt + 1} failed:`, error);

    if (attempt < CALLBACK_MAX_RETRIES - 1) {
      await new Promise(resolve => setTimeout(resolve, CALLBACK_RETRY_DELAYS[attempt]));
      return sendCallback(url, payload, attempt + 1);
    }

    return false;
  }
}

export async function processWebhookJob(jobId: number) {
  try {
    const [job] = await db
      .select()
      .from(webhookJobs)
      .where(eq(webhookJobs.id, jobId))
      .limit(1);

    if (!job) {
      console.error(`Job ${jobId} not found`);
      return;
    }

    await db
      .update(webhookJobs)
      .set({ status: 'processing', processedAt: new Date() })
      .where(eq(webhookJobs.id, jobId));

    let publishedContentId: number | null = null;
    let publishedContentUrl: string | null = null;
    let errorMessage: string | null = null;

    try {
      if (job.contentType === 'blogPost') {
        const data = job.contentData as any;

        const adminUsers = await db
          .select()
          .from(users)
          .where(eq(users.isAdmin, true))
          .limit(1);

        if (adminUsers.length === 0) {
          throw new Error('No admin user found to publish blog post');
        }

        const adminUserId = adminUsers[0].id;

        const cleanContent = stripHtmlTags(data.contentHtml || '');
        const cleanTitle = stripHtmlTags(data.title || '');
        const slug = data.slug || generateSlug(cleanTitle);

        const excerpt = cleanContent.substring(0, 200) + '...';

        const category = data.category || (data.categories && data.categories[0]) || 'Technology';

        const contentData = job.contentData as any;
        const [newPost] = await db.insert(blogPosts).values({
          title: cleanTitle,
          slug: slug,
          content: cleanContent,
          excerpt: excerpt,
          category: category,
          tags: stripHtmlFromArray(data.tags || []),
          featuredImage: data.featuredImageUrl || null,
          isPublished: contentData.publishStatus === 'publish',
          authorId: adminUserId,
          source: 'webhook',
        }).returning();

        publishedContentId = newPost.id;
        publishedContentUrl = `${process.env.REPLIT_DEV_DOMAIN || 'http://localhost:5000'}/blog/${slug}`;
      } else if (job.contentType === 'linkedInPost' || job.contentType === 'instagramStory') {
        errorMessage = `Content type ${job.contentType} is logged but not published (external platform)`;
      }

      await db
        .update(webhookJobs)
        .set({
          status: 'completed',
          publishedContentId,
          publishedContentUrl,
          errorMessage,
          completedAt: new Date(),
        })
        .where(eq(webhookJobs.id, jobId));

      const [webhookRequest] = await db
        .select()
        .from(webhookRequests)
        .where(eq(webhookRequests.id, job.requestId))
        .limit(1);

      if (webhookRequest?.notificationUrl) {
        const callbackPayload = errorMessage ? {
          status: 'success',
          trackingId: job.trackingId,
          idempotencyKey: webhookRequest.idempotencyKey,
          data: {
            message: errorMessage,
          }
        } : {
          status: 'success',
          trackingId: job.trackingId,
          idempotencyKey: webhookRequest.idempotencyKey,
          data: {
            postUrl: publishedContentUrl,
            postId: publishedContentId?.toString(),
          }
        };

        const callbackSuccess = await sendCallback(webhookRequest.notificationUrl, callbackPayload);

        await db
          .update(webhookJobs)
          .set({
            callbackAttempts: CALLBACK_MAX_RETRIES,
            callbackStatus: callbackSuccess ? 'success' : 'failed',
          })
          .where(eq(webhookJobs.id, jobId));
      }

    } catch (processingError: any) {
      console.error(`Job ${jobId} processing failed:`, processingError);

      await db
        .update(webhookJobs)
        .set({
          status: 'failed',
          errorMessage: processingError.message || 'Unknown error',
          completedAt: new Date(),
        })
        .where(eq(webhookJobs.id, jobId));

      const [webhookRequest] = await db
        .select()
        .from(webhookRequests)
        .where(eq(webhookRequests.id, job.requestId))
        .limit(1);

      if (webhookRequest?.notificationUrl) {
        const callbackPayload = {
          status: 'failed',
          trackingId: job.trackingId,
          idempotencyKey: webhookRequest.idempotencyKey,
          error: {
            code: 'PROCESSING_FAILED',
            message: `Failed to create blog post: ${processingError.message}`,
          }
        };

        const callbackSuccess = await sendCallback(webhookRequest.notificationUrl, callbackPayload);

        await db
          .update(webhookJobs)
          .set({
            callbackAttempts: CALLBACK_MAX_RETRIES,
            callbackStatus: callbackSuccess ? 'success' : 'failed',
          })
          .where(eq(webhookJobs.id, jobId));
      }
    }

  } catch (error) {
    console.error(`Fatal error processing job ${jobId}:`, error);
  }
}

client/src/components/RegisterForm.tsx.content:
import { useState } from 'react';
import { useForm } from 'react-hook-form';
import { zodResolver } from '@hookform/resolvers/zod';
import { useMutation } from '@tanstack/react-query';
import { z } from 'zod';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from '@/components/ui/form';
import { Eye, EyeOff, User, Mail, UserPlus } from 'lucide-react';
import { useToast } from '@/hooks/use-toast';
import { apiRequest } from '@/lib/queryClient';
import { insertUserSchema } from '@shared/schema';

// Extend insertUserSchema for frontend validation
const registerFormSchema = insertUserSchema.extend({
  confirmPassword: z.string().min(1, 'Please confirm your password'),
}).refine((data) => data.password === data.confirmPassword, {
  message: "Passwords don't match",
  path: ["confirmPassword"],
});

type RegisterFormData = z.infer<typeof registerFormSchema>;

interface RegisterFormProps {
  onSuccess?: () => void;
  onLoginClick?: () => void;
  showLoginLink?: boolean;
  title?: string;
  description?: string;
}

export function RegisterForm({ 
  onSuccess, 
  onLoginClick, 
  showLoginLink = true,
  title = "Create Account",
  description = "Sign up to access our pricing plans"
}: RegisterFormProps) {
  const { toast } = useToast();

  const form = useForm<RegisterFormData>({
    resolver: zodResolver(registerFormSchema),
    defaultValues: {
      username: '',
      email: '',
      password: '',
      confirmPassword: '',
      firstName: '',
      lastName: '',
      isAdmin: false,
    },
  });

  const [showPassword, setShowPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);

  const registerMutation = useMutation({
    mutationFn: async (data: RegisterFormData) => {
      const { confirmPassword, ...registerData } = data;
      const response = await apiRequest('POST', '/api/auth/register', registerData);
      return response.json();
    },
    onSuccess: (data) => {
      toast({
        title: "Welcome!",
        description: "Your account has been created successfully.",
      });
      // The AuthContext will be updated automatically via session verification
      onSuccess?.();
    },
    onError: (error: any) => {
      const errorMessage = error.message?.includes(':')
        ? error.message.split(':', 2)[1].trim()
        : error.message || 'Registration failed';

      toast({
        title: "Registration Failed",
        description: errorMessage,
        variant: "destructive",
      });
    },
  });

  const onSubmit = (data: RegisterFormData) => {
    registerMutation.mutate(data);
  };

  return (
    <Card className="w-full max-w-md mx-auto">
      <CardHeader className="space-y-1">
        <CardTitle className="text-2xl font-bold text-center flex items-center justify-center gap-2">
          <UserPlus className="h-6 w-6" />
          {title}
        </CardTitle>
        <CardDescription className="text-center">
          {description}
        </CardDescription>
      </CardHeader>
      <CardContent>
        <Form {...form}>
          <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
            <div className="grid grid-cols-2 gap-4">
              <FormField
                control={form.control}
                name="firstName"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>First Name</FormLabel>
                    <FormControl>
                      <Input
                        placeholder="John"
                        {...field}
                        value={field.value || ''}
                        disabled={registerMutation.isPending}
                        data-testid="input-firstname"
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />
              <FormField
                control={form.control}
                name="lastName"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Last Name</FormLabel>
                    <FormControl>
                      <Input
                        placeholder="Doe"
                        {...field}
                        value={field.value || ''}
                        disabled={registerMutation.isPending}
                        data-testid="input-lastname"
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />
            </div>

            <FormField
              control={form.control}
              name="username"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Username *</FormLabel>
                  <FormControl>
                    <div className="relative">
                      <User className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 h-4 w-4" />
                      <Input
                        placeholder="Enter username"
                        className="pl-10"
                        {...field}
                        disabled={registerMutation.isPending}
                        data-testid="input-username"
                      />
                    </div>
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <FormField
              control={form.control}
              name="email"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Email Address *</FormLabel>
                  <FormControl>
                    <div className="relative">
                      <Mail className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 h-4 w-4" />
                      <Input
                        type="email"
                        placeholder="john@example.com"
                        className="pl-10"
                        {...field}
                        value={field.value || ''}
                        disabled={registerMutation.isPending}
                        data-testid="input-email"
                      />
                    </div>
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <FormField
              control={form.control}
              name="password"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Password *</FormLabel>
                  <FormControl>
                    <div className="relative">
                      <Input
                        type={showPassword ? 'text' : 'password'}
                        placeholder="Enter password"
                        className="pr-10"
                        {...field}
                        disabled={registerMutation.isPending}
                        data-testid="input-password"
                      />
                      <Button
                        type="button"
                        variant="ghost"
                        size="sm"
                        className="absolute right-0 top-0 h-full px-3 py-2 hover:bg-transparent"
                        onClick={() => setShowPassword(!showPassword)}
                        disabled={registerMutation.isPending}
                        data-testid="button-toggle-password"
                      >
                        {showPassword ? (
                          <EyeOff className="h-4 w-4 text-gray-400" />
                        ) : (
                          <Eye className="h-4 w-4 text-gray-400" />
                        )}
                      </Button>
                    </div>
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <FormField
              control={form.control}
              name="confirmPassword"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Confirm Password *</FormLabel>
                  <FormControl>
                    <div className="relative">
                      <Input
                        type={showConfirmPassword ? 'text' : 'password'}
                        placeholder="Confirm password"
                        className="pr-10"
                        {...field}
                        disabled={registerMutation.isPending}
                        data-testid="input-confirm-password"
                      />
                      <Button
                        type="button"
                        variant="ghost"
                        size="sm"
                        className="absolute right-0 top-0 h-full px-3 py-2 hover:bg-transparent"
                        onClick={() => setShowConfirmPassword(!showConfirmPassword)}
                        disabled={registerMutation.isPending}
                        data-testid="button-toggle-confirm-password"
                      >
                        {showConfirmPassword ? (
                          <EyeOff className="h-4 w-4 text-gray-400" />
                        ) : (
                          <Eye className="h-4 w-4 text-gray-400" />
                        )}
                      </Button>
                    </div>
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <Button 
              type="submit" 
              className="w-full" 
              disabled={registerMutation.isPending}
              data-testid="button-register"
            >
              {registerMutation.isPending ? 'Creating Account...' : 'Create Account'}
            </Button>

            {showLoginLink && (
              <div className="text-center pt-4">
                <p className="text-sm text-gray-600 dark:text-gray-400">
                  Already have an account?{' '}
                  <Button
                    type="button"
                    variant="link"
                    className="p-0 font-medium"
                    onClick={onLoginClick}
                    data-testid="button-switch-to-login"
                  >
                    Sign in
                  </Button>
                </p>
              </div>
            )}
          </form>
        </Form>
      </CardContent>
    </Card>
  );
}

export default RegisterForm;

