                      {/* Footer with Actions */}
                      <div className="flex items-center justify-between pt-4 border-t border-slate-200/50 dark:border-slate-700/50">
                        <div className="text-xs text-slate-500 dark:text-slate-400">
                          {summary.creditLine}
                        </div>
                        <div className="flex gap-2">
                          <Button
                            variant="ghost"
                            size="sm"
