  --destructive-foreground: hsl(60, 9.1%, 97.8%);
  --ring: hsl(20, 14.3%, 4.1%);
  --radius: 0.5rem;
}

.dark {
  --background: hsl(240, 10%, 3.9%);
  --foreground: hsl(0, 0%, 98%);
  --muted: hsl(240, 3.7%, 15.9%);
  --muted-foreground: hsl(240, 5%, 64.9%);
  --popover: hsl(240, 10%, 3.9%);
  --popover-foreground: hsl(0, 0%, 98%);
  --card: hsl(240, 10%, 3.9%);
  --card-foreground: hsl(0, 0%, 98%);
  --border: hsl(240, 3.7%, 15.9%);
  --input: hsl(240, 3.7%, 15.9%);
  --primary: hsl(207, 90%, 54%);
  --primary-foreground: hsl(211, 100%, 99%);
  --secondary: hsl(240, 3.7%, 15.9%);
  --secondary-foreground: hsl(0, 0%, 98%);
  --accent: hsl(240, 3.7%, 15.9%);
  --accent-foreground: hsl(0, 0%, 98%);
  --destructive: hsl(0, 62.8%, 30.6%);
  --destructive-foreground: hsl(0, 0%, 98%);
  --ring: hsl(240, 4.9%, 83.9%);
  --radius: 0.5rem;
}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased bg-background text-foreground;
    font-family: 'Inter', system-ui, sans-serif;
  }
}

/* Custom styles for Ruvab IT */
.font-inter {
  font-family: 'Inter', system-ui, sans-serif;
}

/* Line clamp utility for text truncation */
.line-clamp-3 {
  display: -webkit-box;
  -webkit-line-clamp: 3;
  -webkit-box-orient: vertical;
  overflow: hidden;
}

.text-primary {
  color: hsl(207, 90%, 54%);
}

.bg-primary {
  background-color: hsl(207, 90%, 54%);
}

.text-accent {
  color: hsl(146, 56%, 52%);
}

.bg-accent {
  background-color: hsl(146, 56%, 52%);
}

.text-secondary {
  color: hsl(220, 13%, 44%);
}

.bg-secondary {
  background-color: hsl(220, 13%, 44%);
}

.bg-background {
  background-color: hsl(0, 0%, 98%);
}

/* AdSense responsive ads */
.adsbygoogle {
  display: block;
  text-align: center;
}

/* Smooth scrolling */
html {
  scroll-behavior: smooth;
}

/* Cookie banner transition */
.cookie-banner {
  transform: translateY(100%);
  transition: transform 0.3s ease-in-out;
}

.cookie-banner.show {
  transform: translateY(0);
}

/* Modal styles */
.modal-overlay {
  backdrop-filter: blur(4px);
}

/* Loading states */
.loading-shimmer {
  background: linear-gradient(90deg, #f0f0f0 25%, #e0e0e0 50%, #f0f0f0 75%);
  background-size: 200% 100%;
  animation: loading 1.5s infinite;
}

@keyframes loading {
  0% {
    background-position: 200% 0;
  }
  100% {
    background-position: -200% 0;
  }
}

/* Responsive typography */
.hero-title {
  font-size: 1.875rem; /* 30px on mobile */
  line-height: 1.2;
}

@media (min-width: 640px) {
  .hero-title {
    font-size: 2.5rem; /* 40px on tablet */
    line-height: 1.2;
  }
}

@media (min-width: 1024px) {
  .hero-title {
    font-size: 3.5rem; /* 56px on desktop */
    line-height: 1.2;
  }
}

@media (min-width: 768px) {
  .hero-title {
    font-size: 3.5rem;
    line-height: 1.1;
  }
}

@media (min-width: 1024px) {
  .hero-title {
    font-size: 4rem;
    line-height: 1.1;
  }
}

/* ReactQuill editor styling */
.ql-editor {
  font-family: 'Inter', system-ui, sans-serif;
  font-size: 14px;
  line-height: 1.6;
  min-height: 200px;
}

.ql-toolbar {
  border-top: 1px solid hsl(var(--border));
  border-left: 1px solid hsl(var(--border));
  border-right: 1px solid hsl(var(--border));
  border-bottom: 1px solid hsl(var(--border));
  background: hsl(var(--background));
}

.ql-container {
  border-left: 1px solid hsl(var(--border));
  border-right: 1px solid hsl(var(--border));
  border-bottom: 1px solid hsl(var(--border));
  background: hsl(var(--background));
}

.dark .ql-editor {
  color: hsl(var(--foreground));
}

.dark .ql-toolbar {
  background: hsl(var(--card));
  border-color: hsl(var(--border));
}

.dark .ql-container {
  background: hsl(var(--card));
  border-color: hsl(var(--border));
}

.dark .ql-toolbar .ql-stroke {
  stroke: hsl(var(--foreground));
}

.dark .ql-toolbar .ql-fill {
  fill: hsl(var(--foreground));
}

.dark .ql-toolbar .ql-picker {
  color: hsl(var(--foreground));
}

.dark .ql-toolbar .ql-picker-options {
  background: hsl(var(--card));
  border: 1px solid hsl(var(--border));
}

.dark .ql-toolbar .ql-picker-item:hover {
  background: hsl(var(--accent));
}

.dark .ql-snow .ql-tooltip {
  background: hsl(var(--card));
  border: 1px solid hsl(var(--border));
  color: hsl(var(--foreground));
}

.dark .ql-snow .ql-tooltip input {
  background: hsl(var(--input));
  border: 1px solid hsl(var(--border));
  color: hsl(var(--foreground));
}

.dark .ql-snow .ql-tooltip a {
  color: hsl(var(--primary));
}

