import OpenAI from 'openai';
import { stripHtmlTags, stripHtmlFromArray } from './html-utils';

const openai = new OpenAI({
  apiKey: process.env.AI_INTEGRATIONS_OPENAI_API_KEY,
  baseURL: process.env.AI_INTEGRATIONS_OPENAI_BASE_URL,
});

export const BLOG_CATEGORIES = [
  'Technology',
  'AI & Machine Learning',
  'Business Intelligence',
  'Automation',
  'Data Analytics',
  'Digital Transformation',
  'Cybersecurity',
  'Cloud Computing',
  'Software Development',
  'Industry News'
] as const;

export type BlogCategory = typeof BLOG_CATEGORIES[number];

interface GeneratedBlog {
  title: string;
  content: string;
  excerpt: string;
  category: string;
  tags: string[];
  slug: string;
  featuredImage: string | null;
}

async function generateBlogImage(title: string, category: string): Promise<string | null> {
  try {
    const imagePrompt = `A professional, modern blog header image representing "${title}". Style: clean, tech-focused, professional business aesthetic with gradient colors (blue, purple, cyan). High quality, 16:9 aspect ratio, minimalist design.`;

    console.log(`Generating featured image for: ${title}`);

    const response = await openai.images.generate({
      model: "dall-e-3",
      prompt: imagePrompt,
      n: 1,
      size: "1792x1024",
      quality: "standard",
    });

    const imageUrl = response.data?.[0]?.url;

    if (imageUrl) {
      console.log(`‚úÖ Image generated successfully for: ${title}`);
      return imageUrl;
    }

    return null;
  } catch (error) {
    console.error('Error generating blog image:', error);
    return null;
  }
}

export async function generateBlogArticle(category: BlogCategory): Promise<GeneratedBlog> {
  const prompt = `You are a recognized industry expert and award-winning technology journalist with 20+ years of hands-on experience creating premium editorial content that meets Google AdSense 2025 E-E-A-T standards (Experience, Expertise, Authoritativeness, Trustworthiness).

CRITICAL GOOGLE ADSENSE 2025 E-E-A-T REQUIREMENTS:
- EXPERIENCE: Demonstrate first-hand, real-world experience with the subject matter
- EXPERTISE: Showcase deep technical knowledge and mastery of the topic
- AUTHORITATIVENESS: Position content as definitive, credible industry reference
- TRUSTWORTHINESS: Provide accurate, well-researched, verifiable information
- Content MUST be 1000-1500+ words (AdSense 2025 minimum for monetization)
- NO generic, thin, or AI-generated feel - write with genuine human expertise

Write a comprehensive, authoritative long-form article about ${category} with these strict requirements:

1. LENGTH & DEPTH (CRITICAL - ADSENSE 2025):
   - 1200-1500 words minimum (substantial, authoritative content)
   - In-depth expert analysis with multiple layers and perspectives
   - Comprehensive coverage that serves as a complete resource
   - Include historical context, current trends, and future predictions

2. E-E-A-T CONTENT QUALITY (MANDATORY FOR ADSENSE 2025):

   **EXPERIENCE:**
   - Share specific, real-world scenarios you've encountered
   - Include practical insights from hands-on implementation
   - Reference actual projects, challenges, and solutions
   - Use first-person perspective where appropriate to show direct experience

   **EXPERTISE:**
   - Demonstrate deep technical understanding and mastery
   - Explain complex concepts clearly with expert-level detail
   - Include industry-specific terminology used correctly
   - Reference frameworks, methodologies, and best practices
   - Cite specific data, statistics, or research findings

   **AUTHORITATIVENESS:**
   - Position the article as the definitive guide on the topic
   - Include references to industry standards and leading practices
   - Present information with confidence and authority
   - Cover the topic comprehensively - leave no important aspects unaddressed

   **TRUSTWORTHINESS:**
   - Provide accurate, verifiable information
   - Be transparent about limitations or evolving areas
   - Include balanced perspectives (pros/cons, benefits/challenges)
   - Use specific examples and real data (not vague generalizations)

3. STRUCTURE (Long-Form Professional Article):
   - Compelling, authoritative headline (60-70 characters, conveys expertise)
   - Strong introduction (3-4 paragraphs) that establishes authority and value
   - 5-7 well-developed H2 sections, each covering a distinct aspect
   - Each section should have 3-4 substantive paragraphs (100+ words per section)
   - Include H3 subheadings within sections for detailed breakdowns
   - Strong conclusion (3-4 paragraphs) with expert recommendations and next steps

4. WRITING STYLE (Expert Voice):
   - Write as a recognized industry expert sharing hard-won insights
   - Professional, authoritative tone with genuine expertise
   - Use active voice and confident, declarative statements
   - Balance technical depth with accessibility
   - Include specific examples, case studies, and real-world applications
   - NO generic advice - every point should demonstrate unique expertise

5. CONTENT ELEMENTS (E-E-A-T Signals):
   - Include specific numbers, percentages, or timeframes
   - Reference industry research, studies, or trends
   - Provide step-by-step guidance or frameworks
   - Share lessons learned from experience
   - Address common mistakes and how to avoid them
   - Include expert tips or pro insights

6. SEO & DISCOVERABILITY:
   - Naturally incorporate relevant keywords throughout
   - Use semantic variations and related technical terms
   - Ensure headline demonstrates expertise and authority
   - Structure content for featured snippets and rich results

7. VALUE PROPOSITION (Reader-First):
   - Every paragraph must provide actionable, expert-level value
   - Answer "how" and "why" in depth, not just "what"
   - Provide insights that save readers time, money, or effort
   - Include implementation guidance, not just theory

Return the article in the following JSON format:
{
  "title": "Expert-level, authoritative headline (60-70 chars) that establishes credibility",
  "content": "Full long-form article (1200-1500 words) in PLAIN TEXT format. Use ## for H2 headings, ### for H3 headings. Use double line breaks between paragraphs. NO HTML TAGS. Demonstrate E-E-A-T throughout with specific examples, expert insights, data points, and actionable guidance. Write as an industry expert sharing definitive knowledge.",
  "excerpt": "Authoritative 2-sentence summary that establishes expertise and value (150-160 characters)",
  "tags": ["expert-level-tag1", "industry-specific-tag2", "technical-tag3", "authority-tag4", "professional-tag5"]
}

REMEMBER: This content MUST meet Google AdSense 2025 standards. Minimum 1000 words. Demonstrate E-E-A-T throughout. Write as a recognized expert with deep experience and authority. Generic, short, or thin content will be REJECTED and fail monetization.`;

  try {
    const completion = await openai.chat.completions.create({
      model: 'gpt-4o',
      messages: [
        {
          role: 'system',
          content: `You are a globally recognized technology expert and thought leader with 20+ years of hands-on industry experience. You create authoritative, long-form editorial content that meets Google AdSense 2025 E-E-A-T standards (Experience, Expertise, Authoritativeness, Trustworthiness).

Your credentials and approach:
- **EXPERIENCE**: You have direct, first-hand experience implementing and working with the technologies you write about. You share real scenarios, challenges, and solutions from your career.
- **EXPERTISE**: You possess deep technical mastery and can explain complex concepts with clarity and precision. You use industry-specific terminology correctly and reference established frameworks and methodologies.
- **AUTHORITATIVENESS**: Your articles are definitive guides that other professionals reference. You present information with confidence backed by knowledge and cover topics comprehensively.
- **TRUSTWORTHINESS**: You provide accurate, verifiable, well-researched information. You're transparent about limitations, present balanced perspectives, and use specific data rather than vague generalizations.

Your writing standards:
- 1200-1500 word minimum - substantial, comprehensive coverage
- Expert-level insights backed by real-world experience
- Specific examples, case studies, data points, and research
- Authoritative tone that establishes credibility
- Original analysis that demonstrates unique expertise
- Professional journalism standards with meticulous research
- Clear structure with 5-7 well-developed sections
- Every paragraph adds expert-level value

You NEVER write generic, thin, or surface-level content. Every article demonstrates genuine expertise, shares hard-won insights, and positions you as a recognized authority in the field. Your work meets the highest standards for Google AdSense 2025 monetization and E-E-A-T requirements.`
        },
        {
          role: 'user',
          content: prompt
        }
      ],
      temperature: 0.7,
      max_tokens: 5000,
      response_format: { type: 'json_object' }
    });

    const responseText = completion.choices[0]?.message?.content;
    if (!responseText) {
      throw new Error('No content generated from OpenAI');
    }

    const parsedResponse = JSON.parse(responseText);

    const slug = generateSlug(parsedResponse.title);
    const cleanTitle = stripHtmlTags(parsedResponse.title);
    const cleanContent = stripHtmlTags(parsedResponse.content);
    const cleanExcerpt = stripHtmlTags(parsedResponse.excerpt);

    // Validate content quality for AdSense 2025 compliance (E-E-A-T standards)
    const wordCount = cleanContent.split(/\s+/).filter(word => word.length > 0).length;

    // Enforce strict 1000-1500+ word requirement per AdSense 2025 guidelines
    if (wordCount < 1000) {
      throw new Error(`Generated content too short (${wordCount} words). AdSense 2025 requires minimum 1000 words (recommended 1500+) for E-E-A-T compliance.`);
    }

    if (wordCount > 2000) {
      throw new Error(`Generated content too long (${wordCount} words). Keep content focused and valuable (1000-1500 words optimal for user engagement).`);
    }

    // Check for proper H2 heading structure (requires 5-7 H2 sections for long-form content)
    const h2Headings = (cleanContent.match(/^## [^\n]+$/gm) || []).length;

    if (h2Headings < 5) {
      throw new Error(`Insufficient content structure (only ${h2Headings} H2 sections). AdSense 2025 requires 5-7 well-developed sections for 1000+ word articles demonstrating E-E-A-T.`);
    }

    if (h2Headings > 8) {
      throw new Error(`Too many H2 sections (${h2Headings}). Content should have 5-7 focused sections for optimal readability and authority.`);
    }

    // Check for substantive content depth (more paragraphs for longer content)
    const paragraphCount = cleanContent.split('\n\n').filter(p => p.trim().length > 50).length;

    if (paragraphCount < 15) {
      throw new Error(`Insufficient content depth (only ${paragraphCount} paragraphs). Each of the 5-7 sections should have 2-4 substantive paragraphs demonstrating expertise.`);
    }

    console.log(`‚úÖ AdSense 2025 quality validation passed: ${wordCount} words, ${h2Headings} H2 sections, ${paragraphCount} paragraphs`);

    // Generate featured image
    const featuredImage = await generateBlogImage(cleanTitle, category);

    return {
      title: cleanTitle,
      content: cleanContent,
      excerpt: cleanExcerpt,
      category: category,
      tags: stripHtmlFromArray(parsedResponse.tags || []),
      slug: slug,
      featuredImage: featuredImage
    };

  } catch (error) {
    console.error('Error generating blog article:', error);
    throw new Error(`Failed to generate blog article for category ${category}: ${error instanceof Error ? error.message : 'Unknown error'}`);
  }
}

function generateSlug(title: string): string {
  return title
    .toLowerCase()
    .replace(/[^a-z0-9]+/g, '-')
    .replace(/^-|-$/g, '')
    .substring(0, 200);
}

export function getNextCategory(lastCategory: string | null): BlogCategory {
  if (!lastCategory) {
    return BLOG_CATEGORIES[0];
  }

  const currentIndex = BLOG_CATEGORIES.indexOf(lastCategory as BlogCategory);

  if (currentIndex === -1) {
    return BLOG_CATEGORIES[0];
  }

  const nextIndex = (currentIndex + 1) % BLOG_CATEGORIES.length;
  return BLOG_CATEGORIES[nextIndex];
}

export function shouldGenerateToday(): boolean {
  const today = new Date();
  const dayOfWeek = today.getDay();

  return dayOfWeek === 1 || dayOfWeek === 3 || dayOfWeek === 5;
}

export function getNextScheduledDate(): Date {
  const today = new Date();
  const dayOfWeek = today.getDay();

  let daysToAdd = 0;

  if (dayOfWeek === 0) {
    daysToAdd = 1;
  } else if (dayOfWeek === 1) {
    daysToAdd = 2;
  } else if (dayOfWeek === 2) {
    daysToAdd = 1;
  } else if (dayOfWeek === 3) {
    daysToAdd = 2;
  } else if (dayOfWeek === 4) {
    daysToAdd = 1;
  } else if (dayOfWeek === 5) {
    daysToAdd = 3;
  } else if (dayOfWeek === 6) {
    daysToAdd = 2;
  }

  const nextDate = new Date(today);
  nextDate.setDate(today.getDate() + daysToAdd);
  nextDate.setHours(10, 0, 0, 0);

  return nextDate;
}

server/news-scheduler.ts.content:
import { storage } from './storage';
import { generateNewsSummary, type NewsSummaryRequest } from './ai-news-summarizer';
import { db } from './db';
import { aiNewsSummaries } from '@shared/schema';

function shouldGenerateToday(): boolean {
  const today = new Date();
  const dayOfWeek = today.getDay(); // 0 = Sunday, 1 = Monday, etc.

  // Generate on Tuesday (2), Thursday (4), Saturday (6)
  return dayOfWeek === 2 || dayOfWeek === 4 || dayOfWeek === 6;
}

export async function checkAndGenerateNewsSummaries(force = false): Promise<{ 
  generated: boolean; 
  message: string; 
  summariesCount?: number;
  summaryIds?: number[];
}> {
  try {
    const schedule = await storage.getNewsSchedule();
    const today = new Date();
    today.setHours(0, 0, 0, 0);

    if (!force && !shouldGenerateToday()) {
      return {
        generated: false,
        message: `Today is not a scheduled generation day. News summaries are published on Tuesdays, Thursdays, and Saturdays only.`
      };
    }

    if (!force && schedule?.lastGeneratedDate) {
      const lastGenDate = new Date(schedule.lastGeneratedDate);
      lastGenDate.setHours(0, 0, 0, 0);

      if (lastGenDate.getTime() === today.getTime()) {
        return {
          generated: false,
          message: 'News summaries already generated today.'
        };
      }
    }

    // Fetch latest trending tech news
    console.log('Fetching trending tech news articles...');
    const newsResponse = await fetch('http://localhost:5000/api/technology-news', {
      headers: {
        'Content-Type': 'application/json',
      },
    });

    if (!newsResponse.ok) {
      throw new Error(`Failed to fetch news: ${newsResponse.statusText}`);
    }

    const newsData = await newsResponse.json();
    const articles = newsData.articles || [];

    if (articles.length === 0) {
      await storage.updateNewsSchedule({
        failedAttempts: (schedule?.failedAttempts || 0) + 1,
        lastFailedRun: new Date(),
      });

      return {
        generated: false,
        message: 'No news articles available to summarize.'
      };
    }

    // Select top 5 articles for summarization
    const topArticles = articles.slice(0, 5);
    console.log(`Generating AI summaries for ${topArticles.length} articles...`);

    const summaryIds: number[] = [];
    let successCount = 0;

    for (const article of topArticles) {
      try {
        // Prepare article data
        const articleRequest: NewsSummaryRequest = {
          title: article.title,
          content: article.content || article.description || article.title,
          url: article.url,
          sourceName: article.source?.name || 'Unknown',
          author: article.author
        };

        // Generate AI summary
        const aiSummary = await generateNewsSummary(articleRequest);

        // Create credit line
        const creditLine = `Source: ${article.source?.name || 'Unknown'} | Original article: ${article.url}`;

        // Save to database
        const [newSummary] = await db.insert(aiNewsSummaries).values({
          originalUrl: article.url,
          originalTitle: article.title,
          originalPublishedAt: article.publishedAt ? new Date(article.publishedAt) : null,
          sourceName: article.source?.name || 'Unknown',
          sourceUrl: article.source?.url || article.url,
          aiSummary: aiSummary.summary,
          keyHighlights: aiSummary.keyHighlights,
          keyTakeaways: aiSummary.keyTakeaways,
          actionItems: aiSummary.actionItems,
          category: 'technology',
          tags: [], // Could extract from article if needed
          wordCount: aiSummary.wordCount,
          featuredImage: aiSummary.featuredImage,
          originalAuthor: article.author || null,
          creditLine,
          aiModel: 'gpt-4o-mini',
          scheduledDate: today,
          isPublished: true,
          publishedAt: new Date(),
        }).returning();

        summaryIds.push(newSummary.id);
        successCount++;

        console.log(`‚úì Generated summary for: ${article.title}`);

        // Small delay to avoid rate limiting
        await new Promise(resolve => setTimeout(resolve, 1500));
      } catch (error) {
        console.error(`Failed to generate summary for "${article.title}":`, error);
        // Continue with next article
      }
    }

    // Update schedule
    await storage.updateNewsSchedule({
      lastGeneratedDate: today,
      totalGenerated: (schedule?.totalGenerated || 0) + successCount,
      lastSuccessfulRun: new Date(),
      failedAttempts: 0,
    });

    return {
      generated: true,
      message: `Successfully generated ${successCount} AI-powered news summaries!`,
      summariesCount: successCount,
      summaryIds
    };
  } catch (error) {
    console.error('News summary generation error:', error);

    // Update failed attempts
    const schedule = await storage.getNewsSchedule();
    await storage.updateNewsSchedule({
      failedAttempts: (schedule?.failedAttempts || 0) + 1,
      lastFailedRun: new Date(),
    });

    throw error;
  }
}

export async function initializeNewsSchedule(): Promise<void> {
  try {
    const existing = await storage.getNewsSchedule();

    if (!existing) {
      await storage.createNewsSchedule({
        isActive: true,
        totalGenerated: 0,
        failedAttempts: 0,
      });
      console.log('‚úÖ News schedule initialized');
    }
  } catch (error) {
    console.error('Error initializing news schedule:', error);
  }
}

let newsSchedulerInterval: NodeJS.Timeout | null = null;

export function startNewsScheduler(): void {
  if (newsSchedulerInterval) {
    console.log('‚ö†Ô∏è  News scheduler already running');
    return;
  }

  const checkInterval = 60 * 60 * 1000; // 1 hour

  const runScheduledCheck = async () => {
    try {
      const now = new Date();
      const hour = now.getHours();

      // Run at 10 AM on Tue/Thu/Sat
      if (hour === 10 && shouldGenerateToday()) {
        console.log('ü§ñ Running scheduled news summary generation...');
        const result = await checkAndGenerateNewsSummaries(false);

        if (result.generated) {
          console.log(`‚úÖ ${result.message}`);
        } else {
          console.log(`‚ÑπÔ∏è  ${result.message}`);
        }
      }
    } catch (error) {
      console.error('‚ùå News scheduler error:', error);
    }
  };

  // Run initial check
  runScheduledCheck();

  // Schedule hourly checks
  newsSchedulerInterval = setInterval(runScheduledCheck, checkInterval);

  console.log('‚úÖ News scheduler started - checking hourly at 10:00 AM on Tue/Thu/Sat');
}

export function stopNewsScheduler(): void {
  if (newsSchedulerInterval) {
    clearInterval(newsSchedulerInterval);
    newsSchedulerInterval = null;
    console.log('‚èπÔ∏è  News scheduler stopped');
  }
}

server/ai-news-summarizer.ts.content:
import OpenAI from 'openai';
import { stripHtmlTags, stripHtmlFromArray } from './html-utils';

const openai = new OpenAI({
  apiKey: process.env.AI_INTEGRATIONS_OPENAI_API_KEY,
  baseURL: process.env.AI_INTEGRATIONS_OPENAI_BASE_URL,
});

export interface NewsSummaryRequest {
  title: string;
  content: string;
  url: string;
  sourceName: string;
  author?: string;
}

export interface NewsSummaryResponse {
  summary: string; // 200-300 words
  keyHighlights: string[]; // 3-5 bullet points
  keyTakeaways: string[]; // 2-4 main insights
  actionItems: string[]; // 1-3 suggested actions
  wordCount: number;
  featuredImage: string | null; // AI-generated image URL
}

async function generateNewsImage(title: string): Promise<string | null> {
  try {
    const imagePrompt = `A modern, professional news article header image for: "${title}". Style: tech news, clean design, professional aesthetic with gradient colors (blue, purple, cyan). High quality, 16:9 aspect ratio, minimalist and engaging.`;

    console.log(`Generating featured image for news: ${title}`);

    const response = await openai.images.generate({
      model: "dall-e-3",
      prompt: imagePrompt,
      n: 1,
      size: "1792x1024",
      quality: "standard",
    });

    const imageUrl = response.data?.[0]?.url;

    if (imageUrl) {
      console.log(`‚úÖ News image generated successfully for: ${title}`);
      return imageUrl;
    }

    return null;
  } catch (error) {
    console.error('Error generating news image:', error);
    return null;
  }
}

export async function generateNewsSummary(article: NewsSummaryRequest): Promise<NewsSummaryResponse> {
  const prompt = `You are a professional technology news analyst. Analyze the following tech news article and provide a comprehensive summary.

ARTICLE DETAILS:
Title: ${article.title}
Source: ${article.sourceName}
${article.author ? `Author: ${article.author}` : ''}

ARTICLE CONTENT:
${article.content}

TASK:
Generate a professional analysis with the following structure:

1. SUMMARY (200-300 words):
   - Write a concise, engaging summary that captures the essence of the article
   - Use clear, accessible language suitable for tech professionals
   - Maintain a neutral, informative tone
   - Include key facts, figures, and developments

2. KEY HIGHLIGHTS (3-5 bullet points):
   - Extract the most important facts or developments
   - Focus on what's new or significant
   - Keep each point concise (1-2 sentences max)

3. KEY TAKEAWAYS (2-4 insights):
   - Identify the broader implications
   - What does this mean for the tech industry/users/businesses?
   - Focus on strategic or practical insights

4. ACTION ITEMS (1-3 suggested actions):
   - What should readers do with this information?
   - Practical next steps (e.g., "Monitor this development," "Consider adopting this technology," "Review your security protocols")
   - Make them actionable and specific

Format your response as JSON with this structure:
{
  "summary": "200-300 word summary text here",
  "keyHighlights": ["highlight 1", "highlight 2", "highlight 3"],
  "keyTakeaways": ["takeaway 1", "takeaway 2"],
  "actionItems": ["action 1", "action 2"]
}

IMPORTANT:
- Keep the summary between 200-300 words
- Be objective and fact-based
- Focus on technology-related aspects
- Use professional but accessible language
- Use PLAIN TEXT only - NO HTML tags`;

  try {
    const completion = await openai.chat.completions.create({
      model: "gpt-4o-mini",
      messages: [
        {
          role: "system",
          content: "You are a professional technology news analyst who creates concise, insightful summaries of tech news articles. You always respond with valid JSON."
        },
        {
          role: "user",
          content: prompt
        }
      ],
      response_format: { type: "json_object" },
      temperature: 0.7,
      max_tokens: 1500,
    });

    const responseText = completion.choices[0]?.message?.content;
    if (!responseText) {
      throw new Error('Empty response from OpenAI');
    }

    const parsed = JSON.parse(responseText);

    // Validate, clean, and strip HTML from the response
    const summary = stripHtmlTags(parsed.summary || '');
    const wordCount = summary.split(/\s+/).filter((word: string) => word.length > 0).length;

    // Generate featured image for the news article
    const featuredImage = await generateNewsImage(article.title);

    return {
      summary,
      keyHighlights: stripHtmlFromArray(Array.isArray(parsed.keyHighlights) ? parsed.keyHighlights : []),
      keyTakeaways: stripHtmlFromArray(Array.isArray(parsed.keyTakeaways) ? parsed.keyTakeaways : []),
      actionItems: stripHtmlFromArray(Array.isArray(parsed.actionItems) ? parsed.actionItems : []),
      wordCount,
      featuredImage
    };
  } catch (error) {
    console.error('Error generating news summary:', error);
    throw new Error(`Failed to generate AI summary: ${error instanceof Error ? error.message : 'Unknown error'}`);
  }
}

export async function batchGenerateNewsSummaries(articles: NewsSummaryRequest[]): Promise<(NewsSummaryResponse & { originalUrl: string })[]> {
  const results: (NewsSummaryResponse & { originalUrl: string })[] = [];

  for (const article of articles) {
    try {
      console.log(`Generating AI summary for: ${article.title}`);
      const summary = await generateNewsSummary(article);
      results.push({
        ...summary,
        originalUrl: article.url
      });

      // Small delay to avoid rate limiting
      await new Promise(resolve => setTimeout(resolve, 1000));
    } catch (error) {
      console.error(`Failed to summarize article "${article.title}":`, error);
      // Continue with other articles even if one fails
    }
  }

  return results;
}

