export function stripHtmlTags(text: string): string {
  if (!text) return '';

  return text
    .replace(/<[^>]*>/g, '')
    .replace(/&nbsp;/g, ' ')
    .replace(/&amp;/g, '&')
    .replace(/&lt;/g, '<')
    .replace(/&gt;/g, '>')
    .replace(/&quot;/g, '"')
    .replace(/&#39;/g, "'")
    .replace(/&[a-z]+;/gi, '')
    .replace(/\s+/g, ' ')
    .trim();
}

export function stripHtmlFromArray(items: string[]): string[] {
  return items.map(item => stripHtmlTags(item));
}
